    private fun saveRootInfo() {
        remotesData.forEach {
            val listOfRemotes = mutableListOf<RemoteButtonDBModel>()
            it.value.forEach { button ->
                listOfRemotes.add(
                    RemoteButtonDBModel(
                        offsetX = 0f,
                        offsetY = 0f,
                        name = button.key,
                        irPattern = IRPatternDecoder(button.value).irPattern
                    )
                )
            }
            viewModelScope.launch(Dispatchers.IO) {
                remoteDataRepository.addRemote(
                    RemoteDataDBModel(
                        id = 0,
                        name = it.key.split("/")[1],
                        type = it.key.split("/")[0],
                        brand = it.key.split("/")[1],
                        added = Date(),
                        offScreenRemoteButtonDBS = listOfRemotes,
                        onScreenRemoteButtonDBS = listOf(
                            RemoteButtonDBModel(
                                offsetX = 0f,
                                offsetY = 0f,
                                name = "POWER TOGGLE",
                                irPattern = IRPatternDecoder(it.value["POWER TOGGLE"]!!).irPattern
                            )

                        )
                    )
                )
            }
        }
    }
